import "./projets.scss";
import { useState } from "react";
import KeyboardArrowDownIcon from "@mui/icons-material/KeyboardArrowDown";

export default function Projets() {
  const [CurrentSl,setCurrentSl] = useState(0);
  const data = [
    {
      id: 1,
      desc:"Application web M2L dans le cadre du BTS SIO",
      title: "Projet Application GestionMatos",
      img: "assets/CaptureGestionMatos.PNG",
      icon: "assets/m2l_logo.jpg",
    },
    {
      id: 2,
      desc: "Site web M2L dans le cadre du BTS SIO",
      title: "Projet Site M2L",
      img: "assets/CaptureM2L.PNG",
      icon: "assets/m2l_logo.jpg",
    },
    {
      id: 3,
      desc: "Veille Technologique sur le GPU dans le cadre du BTS SIO",
      title: "Veille Technologique GPU",
      img: "assets/",
    },
    {
      id: 4,
      desc: "Veille Juridique sur le RGPD dans le cadre du BTS SIO",
      title: "Veille Juridique RGPD",
      img: "assets/",
    },
  ];

  const handleClick = (way)=>{
    way === 'g' ? setCurrentSl(CurrentSl > 0 ? CurrentSl-1 : 2) : 
    setCurrentSl(CurrentSl<data.length -1 ?CurrentSl + 1 : 0);
  }

  return (
    <div className="projets" id="projets">
      <div className="sl" style={{ transform: 'translateX(-${CurrentSl * 100}vw)' }}>
        {data.map((d) => (
        <div className="container">
          <div className="objet">
            <div className="g">
              <div className="gcontainer">
                <div className="imgcontainer">
                  <img src={d.icon}alt="" />
                </div>
                <h2>{d.title}</h2>
                <p>{d.desc}</p>
                <span>Projets</span>
              </div>
            </div>
            <div className="d">
              <img src="assets/CaptureGestionMatos.PNG" alt="" />
            </div>
          </div>
        </div>))}
      </div>
          <button onClick={()=>handleClick("g")}>
      <KeyboardArrowDownIcon className="arrow g" />
      </button>
      <KeyboardArrowDownIcon className="arrow d" onClick={()=>handleClick("d")}/>
    </div>
  );
}
